    @Test
    public void base64Test()throws Exception{
        String text = "data:image/png;base64,";

        if (text.startsWith("data:image")){
            String subText = text.replaceFirst("data:image/.+;base64,", "");
            byte[] decodedBytes = Base64.getDecoder().decode(subText);
            String key = Md5Util.getMd5(text);
            String path = "e:/tmp/"+key+".png";
            FileUtils.writeByteArrayToFile(new File(path), decodedBytes);

            System.out.println(path);
        }
    }

