    public static List<String> getAllList(String link){
        List<String> list = new ArrayList<>();
        try {
            URL url = new URL(link);
            Document parse = Jsoup.parse(url, 20000);


            Element body = parse.body();

            boolean find = false;

            for (int i = 0; i < body.childNodeSize(); i++) {
//            System.out.println("i="+i);
                try {
                    Element child = body.child(i);
                    String s = child.getAllElements().get(0).getAllElements().get(0).toString();
//            System.out.println(s);
                    if (StringUtils.isEmpty(s)) {
                        continue;
                    }
                    list.add(s);
                }catch (Exception ex){

                }
            }

            return list;
        }catch (Exception ex){
            ex.printStackTrace();
        }
        return null;
    }
